type.state_script:
CMSfwFSMConfigurator_valueChanged( string domain, string device,
      string state, string &fwState )
{
  switch(state)
  {
    case "LOADED":
    case "CONFIGURING":
    case "VERIFYING":
    case "MODIFIED":
      fwState = state;    
      break;
    default:
      fwState = "NOT_READY";
      break;
  }
}

type.action_script:
CMSfwFSMConfigurator_doCommand(string domain, string device, string command)
{
  bool showIntermediateStates;
  int verifyDelay, verifyRetries;  

  dpGet(device + ".configuration.verifyDelay", verifyDelay,
        device + ".configuration.verifyRetries", verifyRetries,
        device + ".configuration.showIntermediateStates", showIntermediateStates);

  //calculate a suitable timeout for the DU
  if(showIntermediateStates)
    fwDU_startTimeout(10, domain, device, "NOT_READY");
  else
    fwDU_startTimeout((verifyDelay*verifyRetries) + 10, domain, device, "NOT_READY");

	if (command == "LOAD")
	{
		anytype valueOf_mode;
		fwDU_getCommandParameter(domain, device, "mode", valueOf_mode);
                dpSet(device + ".sendCommand.command", "LOAD", 
                      device + ".sendCommand.parameter", valueOf_mode); 	
	}
	else
	{
                dpSet(device + ".sendCommand.command", command, 
                      device + ".sendCommand.parameter", "");	
  }
}

type.state_script:
FwCaenChannelHV_valueChanged( string domain, string device,
      int actual_dot_status,
      float actual_dot_vMon, string &fwState )
{
	if (
	(actual_dot_status == 1) &&
	(actual_dot_vMon > 2650) )
	{
		fwState = "ON";
	}
	else if (actual_dot_status == 0)
	{
		fwState = "OFF";
	}
	else if (
	(actual_dot_status == 1) &&
  (actual_dot_vMon > 200)&&
	(actual_dot_vMon <= 2650) )
	{
		fwState = "STANDBY";
	}
	else if (actual_dot_status  == 5)
	{
		fwState = "RAMPING_DOWN";
	}
	else if (actual_dot_status == 3)
	{
		fwState = "RAMPING_UP";
	}
	else if (    
	(getBit(actual_dot_status, 3)) ||    //OvC
	(getBit(actual_dot_status, 4)) ||    //OvV 
        (getBit(actual_dot_status, 5)) || //UnV    
        (getBit(actual_dot_status, 12)))  //UnC 
	{
		fwState = "WARNING";
	}
	else if ((getBit(actual_dot_status, 8)) ||	(getBit(actual_dot_status, 9)))
	{
		fwState = "TRIPPED";
	}
	else 
	{
		fwState = "ERROR";
	}

}

type.action_script:
FwCaenChannelHV_doCommand(string domain, string device, string command)
{

      int onoff,OnType;  
      //dyn_string exceptionInfo;
      //dyn_string deviceList;
      //dyn_dyn_mixed recipeObject;
      //dyn_string exceptionInfo;
      int value; 
      string status;
   string sys; 
   sys=getSystemName();
  uint rcpMode;
  bool RcpOK=TRUE;  
 dpGet(sys+"GL1.GEMCAEN.Beam.RcpMode",rcpMode);  
  if (command == "SWITCH_OFF")  
	{

       dpGet(device+".actual.isOn",onoff);
       if(onoff==1)
       dpSet(device+".settings.onOff:_original.._value",0);
       fwDU_getState(domain,device,status);
       fwDU_setState(domain,device,status);

   }  
	if (command == "SWITCH_ON")    
	{    
     if(rcpMode!=1)        //Mode 2 to is Standby and all Hardware contains Stby settings so needs to update to ON settings
     {
      RcpOK=ChangeBMHV();
      rcpMode=1;
      dpSet(sys+"GL1.GEMCAEN.Beam.RcpMode",rcpMode);  
      DebugN("In Function App ON Rcp");
     }      
      if(RcpOK)
      {
       SwitchOn(device);
       status="ON";
      }
      else
      {
       fwDU_getState(domain,device,status);
      }
      fwDU_setState(domain,device,status);      

  }

  if (command == "GO_TO_STANDBY")    
	 {
     //dyn_string dpe1,dpe2;

     if(rcpMode!=2)        //Mode 1 to is ON and all Hardware contains On mode settings (cosmic or Physics)
     {
      RcpOK=ApplyStbRcp();
      rcpMode=2; 
      dpSet(sys+"GL1.GEMCAEN.Beam.RcpMode",rcpMode);       
      DebugN("In Function App stb Rcp");
     }
     if(RcpOK)
      {
       SwitchToStby(device);
       status="STANDBY";
      }
      else
      {
       fwDU_getState(domain,device,status);
      }
      fwDU_setState(domain,device,status);
       //dpGet(sys+"GL1.GEMCAEN.Beam.RcpAction",rcpAction); 
      // dpSet(sys+"GL1.GEMCAEN.Beam.StandBy",true);

    }
}

type.state_script:
FwCaenChannelLV_valueChanged( string domain, string device,
      int actual_dot_status, string &fwState )
{
	if(actual_dot_status==0)   
	{   
		fwState = "OFF";    
	}   
	else if (actual_dot_status==1)
  {

       fwState = "ON";
       //fwDU_setParameter(domain,device,"enabled",1);
  } 
  else if((getBit(actual_dot_status, 8)) ||      
	(getBit(actual_dot_status, 9)))   
	{     
		fwState = "TRIPPED";     
	}    
	else if (    
	(getBit(actual_dot_status, 3)) ||    
	(getBit(actual_dot_status, 4)) ||     
        (getBit(actual_dot_status, 5)) ||     
        (getBit(actual_dot_status, 12)))    
	 {    
		  fwState = "WARNING";
 	 }    
	else    
	{    
	  fwState = "ERROR";    
	} 
}

type.action_script:
FwCaenChannelLV_doCommand(string domain, string device, string command)
{
	if (command == "SWITCH_OFF")
	{
		dpSet(device+".settings.onOff",0);
	}
	if (command == "SWITCH_ON")
	{
		SwitchOn(device);
	}
	if (command == "GO_TO_STANDBY")
	{
		SwitchOn(device);
	}
}

